
import "./New.css";

import { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import EditEmployee from "./editEmployee.jsx";
import AddEmployee from "./AddEmployee.jsx";

export default function Employee() {
  const [employee, setEmployee] = useState([
    {
      id:1,
      name: "Abebe",
      Depart: "Electrical Engineering",
      image:"data:image/jpeg;base64,",
    },
    {
      id: 2,
      name: "Kebedech",
      Depart: "Mechanical Engineering",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 3,
      name: "Abel",
      Depart: "Software Engineering",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 4,
      name: "Giyon",
      Depart: "Civil Engineering",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 5,
      name: "Robel",
      Depart: "Software Engineering",
      image:
        "data:image/jpeg;base64,",
    },
    {
      id: 6,
      name: "Meron",
      Depart: "Civil Engineering",
      image:
        "data:image/jpeg;base64,",
    },
  ]);
  function updateEmployee(id, newName, newDepart,newImg) {
    const Updated = employee.map((employees) => {
      if (id === employees.id) {
        return { ...employees, name: newName, Depart: newDepart,image: newImg };
      }
      return employees;
    });
    setEmployee(Updated);
  }

  function NewEmployee(Name, Depart, imgUrl) {
    const newEmployee = {
      id: uuidv4(),
      name: Name,
      depart: Depart,
      image: imgUrl,
    };
    setEmployee([...employee, newEmployee]);
  }
  return (
  <div className="Emp">

        <AddEmployee NewEmployee={NewEmployee} />
            <div
              style={{
                display: "flex",
                flexWrap: "wrap",
                width: "100%",
                marginLeft: "20px",
                marginTop:30
               
              }}
            >
              {employee.map((emp) => {
                const editEmployee = (
                  <EditEmployee
                    id={emp.id}
                    name={emp.name}
                    Depart={emp.Depart}
                    img={emp.image}
                    updateEmployee={updateEmployee}
                  />
                );
                return (
                    <div className="card-containers"  key={emp.id} id={emp.id}>
                    <div className="cards">
                    <img src={emp.image} />
                      <div className="details">
                        <div className="title">
                          <h3>{emp.name}</h3>
                          <p>{emp.Depart}</p>
                        </div>
                        {editEmployee}
                      </div>
                    </div>
                   
                  </div>
                 
                );
              })}
            </div>
      </div>
   
  );
}
